const formulario=document.getElementById("formulario"),expresion=document.getElementById("expresion"),texto=document.getElementById("texto"),advertencia=document.getElementById("advertencia"),limpiarBtn=document.getElementById("limpiar"),mostrarMensaje=(e,t)=>{advertencia.innerHTML=`<div style="background-color: ${t}; padding: 10px; border-radius: 5px; color: black; font-weight: bold;">${e}</div>`};formulario.onsubmit=(e=>{e.preventDefault();const[t,n]=[expresion.value.trim(),texto.value.trim()],o=[t&&n?new RegExp(t).test(n)&&/^[a-zA-Z0-9\s\W_]+$/.test(n)?"✔ La expresión es correcta y coincide con el texto.":"❌ La expresión no coincide con el texto o no es válida.":"⚠ Necesitas ingresar una expresión y un texto."],i=[t&&n?new RegExp(t).test(n)?"lightgreen":"lightcoral":"lightyellow"];mostrarMensaje(o[0],i[0])}),limpiarBtn.onclick=(()=>{[expresion.value,texto.value,advertencia.innerHTML]=["","",""],mostrarMensaje("✔ Limpieza de inputs exitosa.","lightblue")});